/* SUD WEB - 2011 */

/* SAME HEIGHT ON BLOCKS (ALL BROWSERS) */
function mySameHeight(elt) {
	var heightBlockMax=0;
	jQuery(elt).each(function(){ if( jQuery(this).height() > heightBlockMax ) { heightBlockMax = jQuery(this).height(); } }); // get max height
	if (jQuery.browser.msie) {
		jQuery(elt).each(function(){ jQuery(this).css("height",heightBlockMax); }); // assign max height
	} else {
		jQuery(elt).each(function(){ jQuery(this).css("min-height",heightBlockMax); }); // assign max height
	}
	heightBlockMax=0;
}

/* ENABLE ROLLOVER ON TARGETED IMAGES (ALL BROWSERS) */
function imgRollover(elt) {
    jQuery(elt).live('mouseover focus', function() { jQuery(this).attr("src", jQuery(this).attr("src").replace("-off","-on")); })
		.live('mouseout blur', function () { jQuery(this).attr("src", jQuery(this).attr("src").replace("-on","-off")); });
}

/* TOGGLEVAL ON INPUTS */
function myToggleVal(elt) {
	jQuery(elt).toggleVal();
}

/* CAROUSELS */
function myCarousel(elt,pagerElt,nbrToShow) {
	jQuery(pagerElt).before('<div class="newLists" />')
	var $bigList = jQuery(elt+' ul'),
	group = $bigList.find('li:lt('+nbrToShow+')').remove(),
	myCounter = 0;
	/* separating in smaller lists */
	while(group.length) {
		jQuery(elt+' .newLists').append('<div class="newList newList'+myCounter+'" />');
		jQuery('<ul />').append(group).appendTo(elt+' .newList'+myCounter);
		group = $bigList.find('li:lt('+nbrToShow+')').remove();
		myCounter++;
	}
	/* remove original container */
	jQuery(elt+' .origList').remove();
	/* once ready, carousel */
	jQuery(elt+' .newLists').cycle({
	    fx:     'scrollUp',
	    speed: 1000,
	    timeout: 3000,
		pause: 1
	});
}

/* FUNCTIONS ON CONTACT PAGE */
function myContactPage() {
	jQuery("#contactForm").validate({
		errorElement: "em",
		highlight: function(element, errorClass, validClass) {
			jQuery(element).addClass(errorClass).removeClass(validClass);
			jQuery(element.form).find("label[for=" + element.id + "]").addClass(errorClass);
		},
		unhighlight: function(element, errorClass, validClass) {
			jQuery(element).removeClass(errorClass).addClass(validClass);
			jQuery(element.form).find("label[for=" + element.id + "]").removeClass(errorClass);
		},
		onfocusout: function(element) { jQuery(element).valid(); },
		onkeyup: function(element) { jQuery(element).valid(); }
	});
}

/* FUNCTIONS ON ALL PAGES */
function myInitPages() {
	jQuery('body').addClass('hasJS');
	imgRollover('.swapImg');
	mySameHeight('.cols');
	myToggleVal('.swapValue');
    jQuery('a[rel="external"]').click( function() { window.open( jQuery(this).attr('href') ); return false; });
	/* back to top */
	jQuery('#back2Top a').click( function() {
		jQuery('html, body').animate({scrollTop:0}, 'slow');
		return false;
    });
}

jQuery(document).ready(function() {

	myInitPages();

	if (jQuery('#contactForm').size() > 0) { myContactPage(); }

	if (jQuery('#partnersList li').size() > 1) {
		myCarousel("#partnersList #partnersListContainer","#partnersList .carouselNav",3);
	}

	/* if (jQuery('#staffList').size() > 0) { mySameHeight('#staffList li'); } */

});

